import React, { useState } from 'react';
import axios from 'axios';

const RequestForm = () => {
  const [formData, setFormData] = useState({
    name: '',
    disconnected_path: '',
    file_folder_name: '',
    description: '',
  });

  const handleChange = (e) => {
    setFormData({...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://<YOUR_BACKEND_IP>:8000/requests", formData);  // Replace IP
      alert("Request submitted successfully!");
      setFormData({ name: '', disconnected_path: '', file_folder_name: '', description: '' });
    } catch (error) {
      alert("Submission failed. Check your network or backend.");
      console.error(error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Submit File/Folder Request</h2>

      <input
        type="text"
        name="name"
        placeholder="Name"
        value={formData.name}
        onChange={handleChange}
        required
      />

      <input
        type="text"
        name="disconnected_path"
        placeholder="Disconnected Path (e.g. P:/Folder)"
        value={formData.disconnected_path}
        onChange={handleChange}
        required
      />

      <input
        type="text"
        name="file_folder_name"
        placeholder="File or Folder Name"
        value={formData.file_folder_name}
        onChange={handleChange}
        required
      />

      <textarea
        name="description"
        placeholder="Description"
        value={formData.description}
        onChange={handleChange}
        rows="3"
        required
      />

      <button type="submit">Submit Request</button>
    </form>
  );
};

export default RequestForm;
